import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
  Paper,
} from '@mui/material';
import Navbar from '../components/Navbar';
import Sidebar from '../components/Sidebar';
import workshops from '../data/workshops';
import { UserContext } from '../contexts/UserContext';

export default function TrainerDashboard() {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();

  // Filter workshops belonging to the current trainer
  const myWorkshops = workshops.filter((w) => w.trainerName === currentUser?.name);

  const menuItems = [{ label: 'My Workshops', path: '/trainer/dashboard' }];

  return (
    <Box sx={{ display: 'flex', minHeight: '100vh', backgroundColor: '#f5f5f5' }}>
      <Navbar />
      <Sidebar items={menuItems} />

      <Box component="main" sx={{ flexGrow: 1, p: 4 }}>
        {/* Header Section */}
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            mb: 3,
          }}
        >
          <Typography variant="h4" fontWeight="bold">
            My Workshops
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate('/trainer/workshops/create')}
          >
            + Create Workshop
          </Button>
        </Box>

        {/* Workshop Cards */}
        {myWorkshops.length === 0 ? (
          <Paper sx={{ p: 3, textAlign: 'center', backgroundColor: '#fff' }}>
            <Typography variant="body1" color="text.secondary">
              You have not created any workshops yet.
            </Typography>
          </Paper>
        ) : (
          <Grid container spacing={3}>
            {myWorkshops.map((workshop) => (
              <Grid item xs={12} sm={6} md={4} key={workshop.id}>
                <Card elevation={3} sx={{ borderRadius: 2 }}>
                  <CardContent>
                    <Typography variant="h6" fontWeight="bold">
                      {workshop.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                      Students Enrolled: {workshop.students.length}
                    </Typography>
                  </CardContent>
                  <CardActions sx={{ justifyContent: 'flex-end' }}>
                    <Button
                      size="small"
                      onClick={() => navigate(`/workshops/${workshop.id}`)}
                    >
                      View
                    </Button>
                    <Button
                      size="small"
                      onClick={() => navigate(`/trainer/workshops/edit/${workshop.id}`)}
                    >
                      Edit
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Box>
    </Box>
  );
}
