import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Navbar from '../components/Navbar';
import Sidebar from '../components/Sidebar';
import workshops from '../data/workshops';
import users from '../data/users';
import { UserContext } from '../contexts/UserContext';

// Dashboard for trainers.  Lists workshops they own and allows them to
// create or edit a workshop.  For simplicity we do not persist
// changes.
export default function TrainerDashboard() {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();

  // Filter workshops belonging to the current trainer
  const myWorkshops = workshops.filter((w) => w.trainerName === currentUser?.name);

  const menuItems = [
    { label: 'My Workshops', path: '/trainer/dashboard' },
    // In a more sophisticated app you might have additional items
  ];

  return (
    <Box sx={{ display: 'flex' }}>
      <Navbar />
      <Sidebar items={menuItems} />
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="h5" gutterBottom>My Workshops</Typography>
          <Button variant="contained" onClick={() => navigate('/trainer/workshops/create')}>Create Workshop</Button>
        </Box>
        <Grid container spacing={2}>
          {myWorkshops.map((workshop) => (
            <Grid item xs={12} md={6} key={workshop.id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{workshop.title}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Students Enrolled: {workshop.students.length}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" onClick={() => navigate(`/workshops/${workshop.id}`)}>View</Button>
                  <Button size="small" onClick={() => navigate(`/trainer/workshops/edit/${workshop.id}`)}>Edit</Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
          {myWorkshops.length === 0 && (
            <Typography variant="body1">You have not created any workshops yet.</Typography>
          )}
        </Grid>
      </Box>
    </Box>
  );
}
