import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
} from 'reactflow';

import 'reactflow/dist/style.css';

export default function WorkshopFlowDesigner() {
  // ✅ Initial node (Start Workshop)
  const initialNodes = [
    {
      id: '1',
      type: 'input', // Input node is the starting point
      data: { label: 'Start Workshop' },
      position: { x: 250, y: 0 },
    },
  ];

  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState([]);

  // ✅ Handle node movement/changes
  const onNodesChange = useCallback((changes) => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === changes.id ? { ...node, position: changes.position } : node
      )
    );
  }, []);

  // ✅ Connect nodes by dragging edges between them
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  return (
    <div style={{ width: '100%', height: '100vh', background: '#f4f4f4' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onConnect={onConnect}
        fitView
      >
        {/* ✅ Helper Tools */}
        <MiniMap />
        <Controls />
        <Background color="#aaa" gap={16} />
      </ReactFlow>
    </div>
  );
}
