import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Navbar from '../components/Navbar';
import Sidebar from '../components/Sidebar';
import workshops from '../data/workshops';

/**
 * Page for trainers to create a new workshop or edit an existing one.
 * This demo does not persist changes; upon saving it simply
 * navigates back to the trainer dashboard with an alert.  If an
 * ID is provided in the route, the form is prefilled with that
 * workshop's details.
 */
export default function CreateEditWorkshopPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const editing = Boolean(id);
  const existing = workshops.find((w) => w.id.toString() === id);

  const [title, setTitle] = useState(existing?.title || '');
  const [description, setDescription] = useState(existing?.description || '');
  const [price, setPrice] = useState(existing?.price || '');
  const [agenda, setAgenda] = useState(existing?.agenda || '');
  const [ppt, setPpt] = useState(null);

  const menuItems = [
    { label: 'My Workshops', path: '/trainer/dashboard' },
    { label: editing ? 'Edit Workshop' : 'Create Workshop', path: '#' }
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real app you would send this data to the backend.  Here we
    // simply show an alert and navigate back to the dashboard.
    alert(`${editing ? 'Updated' : 'Created'} workshop: ${title}`);
    navigate('/trainer/dashboard');
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Navbar />
      <Sidebar items={menuItems} />
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h5" gutterBottom>{editing ? 'Edit Workshop' : 'Create Workshop'}</Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            fullWidth
            margin="normal"
            required
          />
          <TextField
            label="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            fullWidth
            margin="normal"
            multiline
            required
          />
          <TextField
            label="Price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            fullWidth
            margin="normal"
            required
          />
          <TextField
            label="Agenda"
            value={agenda}
            onChange={(e) => setAgenda(e.target.value)}
            fullWidth
            margin="normal"
            multiline
            required
          />
          <Box sx={{ mt: 2 }}>
            <Button variant="contained" component="label">
              Upload PPT
              <input type="file" hidden onChange={(e) => setPpt(e.target.files[0])} />
            </Button>
            {ppt && (
              <Typography variant="body2" sx={{ ml: 2 }}>{ppt.name}</Typography>
            )}
          </Box>
          <Button type="submit" variant="contained" sx={{ mt: 3 }}>{editing ? 'Update' : 'Create'}</Button>
        </form>
      </Box>
    </Box>
  );
}
