import React, { useState, useCallback } from "react";
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  MiniMap,
  Controls,
  Background,
} from "reactflow";
import "reactflow/dist/style.css";

export default function WorkshopFlowDesigner() {
  const [nodes, setNodes] = useState([
    {
      id: "1",
      type: "input",
      position: { x: 250, y: 50 }, // âœ… Must have valid x and y
      data: { label: "ðŸš€ Start Workshop" },
    },
    {
      id: "2",
      type: "default",
      position: { x: 250, y: 150 }, // âœ… Another valid position
      data: { label: "ðŸ“˜ Introduction Section" },
    },
  ]);

  const [edges, setEdges] = useState([
    { id: "e1-2", source: "1", target: "2", animated: true },
  ]);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  return (
    <div style={{ width: "100%", height: "100vh" }}>
      <ReactFlowProvider>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={setNodes}
          onEdgesChange={setEdges}
          onConnect={onConnect}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </ReactFlowProvider>
    </div>
  );
}
