import React, { useCallback, useState } from "react";
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  Background,
  Controls,
} from "reactflow";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "start",
    type: "input",
    data: { label: "Start Workshop" },
    position: { x: 250, y: 0 },
  },
];

let id = 1;
const getId = () => `node_${id++}`;

export default function WorkshopFlowDesigner() {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState([]);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = "move";
  }, []);

  const onDrop = useCallback(
    (event) => {
      event.preventDefault();
      const type = event.dataTransfer.getData("application/reactflow");
      if (!type || !reactFlowInstance) return;

      const position = reactFlowInstance.project({
        x: event.clientX - 250,
        y: event.clientY - 50,
      });

      const newNode = {
        id: getId(),
        type: "default",
        position,
        data: { label: type },
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [reactFlowInstance]
  );

  const onSave = () => {
    console.log("Flow Data: ", { nodes, edges });
    alert("âœ… Flow Saved! Check console.");
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        background: "#f1f3f5",
      }}
    >
      {/* Sidebar */}
      <div
        style={{
          width: 200,
          background: "#fff",
          borderRight: "1px solid #ccc",
          padding: 10,
        }}
      >
        <h4 style={{ textAlign: "center" }}>Workshop Blocks</h4>
        {["Introduction", "Concept", "Code Editor", "Quiz", "Certificate"].map(
          (block) => (
            <div
              key={block}
              style={{
                background: "#f8f9fa",
                border: "1px solid #ccc",
                borderRadius: 5,
                padding: 8,
                marginBottom: 8,
                textAlign: "center",
                cursor: "grab",
              }}
              draggable
              onDragStart={(event) =>
                event.dataTransfer.setData("application/reactflow", block)
              }
            >
              {block}
            </div>
          )
        )}
        <button
          onClick={onSave}
          style={{
            width: "100%",
            padding: 8,
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
          }}
        >
          ðŸ’¾ Save Flow
        </button>
      </div>

      {/* Canvas */}
      <div style={{ flex: 1 }}>
        <ReactFlowProvider>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={setNodes}
            onEdgesChange={setEdges}
            onConnect={onConnect}
            onInit={setReactFlowInstance}
            onDrop={onDrop}
            onDragOver={onDragOver}
            fitView
            style={{ width: "100%", height: "100%" }}
          >
            <Background />
            <Controls />
          </ReactFlow>
        </ReactFlowProvider>
      </div>
    </div>
  );
}
