import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, Card, CardContent, CardActions, Button, Grid } from "@mui/material";
import DashboardLayout from "../components/DashboardLayout";
import { UserContext } from "../contexts/UserContext";
import axios from "axios";

export default function TrainerDashboard() {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [myWorkshops, setMyWorkshops] = useState([]);

  useEffect(() => {
    const fetchWorkshops = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8001/workshops/");
        const filtered = response.data.filter((w) => w.trainer_id === currentUser?.id);
        setMyWorkshops(filtered);
      } catch (error) {
        console.error("Error fetching workshops:", error);
      }
    };
    fetchWorkshops();
  }, [currentUser]);

  const menuItems = [
    { label: "My Workshops", path: "/trainer/dashboard" },
    { label: "Create Workshop", path: "/trainer/workshops/create" },
  ];

  return (
    <DashboardLayout menuItems={menuItems}>
      <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center", mb: 3 }}>
        <Typography variant="h4" fontWeight="bold" color="primary">
          My Workshops
        </Typography>
      </Box>

      {myWorkshops.length === 0 ? (
        <Typography variant="body1" color="text.secondary">
          You have not created any workshops yet.
        </Typography>
      ) : (
        <Grid container spacing={3}>
          {myWorkshops.map((workshop) => (
            <Grid item xs={12} sm={6} md={4} key={workshop.id}>
              <Card elevation={3} sx={{ borderRadius: 2 }}>
                <CardContent>
                  <Typography variant="h6" fontWeight="bold">
                    {workshop.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                    Sections: {workshop.sections.length || 0}
                  </Typography>
                </CardContent>
                <CardActions sx={{ justifyContent: "flex-end" }}>
                  <Button size="small" onClick={() => navigate(`/workshops/${workshop.id}`)}>
                    View
                  </Button>
                  <Button size="small" onClick={() => navigate(`/trainer/workshops/edit/${workshop.id}`)}>
                    Edit
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </DashboardLayout>
  );
}
