import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
} from "@mui/material";
import axios from "axios";
import { UserContext } from "../contexts/UserContext";
import DashboardLayout from "../components/DashboardLayout";

export default function TrainerDashboard() {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [myWorkshops, setMyWorkshops] = useState([]);

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_WORKSHOP_API_URL}/workshops/`)
      .then((res) => {
        const workshops = res.data.filter(
          (w) => w.trainer_id === currentUser?.id
        );
        setMyWorkshops(workshops);
      })
      .catch((err) => console.error("Error fetching workshops:", err));
  }, [currentUser?.id]);

  const menuItems = [
    { label: "My Workshops", path: "/trainer/dashboard" },
    { label: "Create Workshop", path: "/trainer/workshops/create" },
  ];

  return (
    <DashboardLayout menuItems={menuItems}>
      <Box
        sx={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          mb: 3,
        }}
      >
        <Typography variant="h4" fontWeight="bold" color="primary">
          My Workshops
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate("/trainer/workshops/create")}
        >
          + Create Workshop
        </Button>
      </Box>

      {myWorkshops.length === 0 ? (
        <Typography variant="body1" color="text.secondary">
          You have not created any workshops yet.
        </Typography>
      ) : (
        <Grid container spacing={3} justifyContent="flex-start">
          {myWorkshops.map((workshop) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={workshop.id}>
              <Card
                elevation={4}
                sx={{
                  borderRadius: 3,
                  height: "100%",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                  transition: "0.3s",
                  "&:hover": {
                    transform: "translateY(-5px)",
                    boxShadow: 6,
                  },
                }}
              >
                <CardContent>
                  <Typography variant="h6" fontWeight="bold">
                    {workshop.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    sx={{ mt: 1 }}
                  >
                    Sections: {workshop.sections?.length || 0}
                  </Typography>
                </CardContent>
                <CardActions sx={{ justifyContent: "flex-end" }}>
                  <Button
                    size="small"
                    onClick={() => navigate(`/workshops/${workshop.id}`)}
                  >
                    View
                  </Button>
                  <Button
                    size="small"
                    onClick={() =>
                      navigate(`/trainer/workshops/edit/${workshop.id}`)
                    }
                  >
                    Edit
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </DashboardLayout>
  );
}
