import React, { useCallback, useState } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  addEdge,
} from 'reactflow';
import 'reactflow/dist/style.css';

const initialNodes = [
  {
    id: "start",
    type: "default",
    data: { label: "Start Workshop" },
    position: { x: 250, y: 50 },
  },
];

let id = 1;
const getId = () => `node_${id++}`;

export default function WorkshopFlowDesigner() {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState([]);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = "move";
  }, []);

  const onDrop = useCallback(
    (event) => {
      event.preventDefault();
      const type = event.dataTransfer.getData("application/reactflow");
      if (!type || !reactFlowInstance) return;

      const position = reactFlowInstance.project({
        x: event.clientX - 250,
        y: event.clientY - 50,
      });

      const newNode = {
        id: getId(),
        type: "default",
        position,
        data: { label: type },
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [reactFlowInstance]
  );

  const onSave = () => {
    const flowData = { nodes, edges };
    console.log("Saved Flow Data: ", flowData);
    alert("âœ… Flow Saved! Check console for data.");
    // TODO: Save to backend via Axios POST request
  };

  return (
    <div className="flow-designer">
      <ReactFlowProvider>
        {/* Sidebar */}
        <div className="sidebar">
          <h4>Workshop Blocks</h4>
          {["Introduction", "Concept", "Code Editor", "Quiz", "Certificate"].map(
            (block) => (
              <div
                key={block}
                className="draggable-block"
                draggable
                onDragStart={(event) =>
                  event.dataTransfer.setData("application/reactflow", block)
                }
              >
                {block}
              </div>
            )
          )}
          <button className="save-btn" onClick={onSave}>
            ðŸ’¾ Save Flow
          </button>
        </div>

        {/* Canvas */}
        <div className="reactflow-wrapper">
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={setNodes}
            onEdgesChange={setEdges}
            onConnect={onConnect}
            onInit={setReactFlowInstance}
            onDrop={onDrop}
            onDragOver={onDragOver}
            fitView
          >
            <Background />
            <Controls />
          </ReactFlow>
        </div>
      </ReactFlowProvider>
    </div>
  );
}
