import React, { useContext, useEffect, useState } from "react";
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
  CircularProgress,
  Paper,
} from "@mui/material";
import axios from "axios";
import { UserContext } from "../contexts/UserContext";
import DashboardLayout from "../components/DashboardLayout";
import CreateWorkshopForm from "./CreateWorkshopForm";

export default function TrainerDashboard() {
  const { currentUser } = useContext(UserContext);
  const [workshops, setWorkshops] = useState([]);
  const [loading, setLoading] = useState(false);
  const [activeView, setActiveView] = useState("list"); // list or create

  const menuItems = [
    { label: "My Workshops", path: "#", onClick: () => setActiveView("list") },
    { label: "Create Workshop", path: "#", onClick: () => setActiveView("create") },
  ];

  const fetchWorkshops = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://127.0.0.1:8001/workshops/");
      setWorkshops(response.data);
    } catch (err) {
      console.error("Failed to fetch workshops", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWorkshops();
  }, []);

  return (
    <DashboardLayout menuItems={menuItems}>
      {activeView === "create" ? (
        <CreateWorkshopForm
          onSuccess={() => {
            fetchWorkshops();
            setActiveView("list");
          }}
          onCancel={() => setActiveView("list")}
        />
      ) : (
        <Box>
          <Box sx={{ display: "flex", justifyContent: "space-between", mb: 3 }}>
            <Typography variant="h4" color="primary" fontWeight="bold">
              My Workshops
            </Typography>
            <Button
              variant="contained"
              color="primary"
              onClick={() => setActiveView("create")}
            >
              + Create Workshop
            </Button>
          </Box>

          {loading ? (
            <CircularProgress />
          ) : workshops.length === 0 ? (
            <Typography variant="body1" color="text.secondary">
              You have not created any workshops yet.
            </Typography>
          ) : (
            <Grid container spacing={3}>
              {workshops.map((workshop) => (
                <Grid item xs={12} sm={6} md={4} key={workshop.id}>
                  <Card elevation={3} sx={{ borderRadius: 2 }}>
                    <CardContent>
                      <Typography variant="h6" fontWeight="bold">
                        {workshop.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                        Sections: {workshop.sections?.length || 0}
                      </Typography>
                    </CardContent>
                    <CardActions sx={{ justifyContent: "flex-end" }}>
                      <Button
                        size="small"
                        onClick={() => alert("View details page coming soon")}
                      >
                        View
                      </Button>
                      <Button
                        size="small"
                        onClick={() => alert("Edit functionality coming soon")}
                      >
                        Edit
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </Box>
      )}
    </DashboardLayout>
  );
}
