import React, { useState, useCallback } from "react";
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  addEdge,
  applyNodeChanges,
  applyEdgeChanges,
} from "reactflow";
import "reactflow/dist/style.css";

export default function WorkshopFlowDesigner() {
  const [nodes, setNodes] = useState([
    {
      id: "1",
      type: "default",
      position: { x: 300, y: 50 },
      data: { label: "Start Workshop", metadata: { description: "", pptUrl: "" } },
    },
  ]);

  const [edges, setEdges] = useState([]);

  // ✅ Handle Node Changes safely
  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => applyNodeChanges(changes, nds)),
    []
  );

  // ✅ Handle Edge Changes safely
  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    []
  );

  // ✅ Add edges on connect
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  // ✅ Add a new node (with safe default position)
  const addNode = (label) => {
    const newId = (nodes.length + 1).toString();
    setNodes((nds) => [
      ...nds,
      {
        id: newId,
        type: "default",
        position: {
          x: 300 + Math.random() * 100,
          y: 100 + nds.length * 100,
        },
        data: {
          label,
          metadata: {
            description: `${label} description`,
            pptUrl: "",
            code: "",
            questions: [],
          },
        },
      },
    ]);
  };

  // ✅ Save Flow to LocalStorage (can be replaced with API call)
  const saveFlow = () => {
    const flowData = { nodes, edges };
    localStorage.setItem("workshopFlow", JSON.stringify(flowData));
    alert("✅ Workshop flow saved successfully!");
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Left Sidebar for Drag/Click Add */}
      <div
        style={{
          width: "220px",
          background: "#f5f5f5",
          padding: "10px",
          borderRight: "1px solid #ddd",
        }}
      >
        <h3 style={{ marginBottom: "10px" }}>Drag Nodes</h3>
        {["Section", "Concept Explanation", "Code Editor", "Quiz", "Certificate"].map(
          (label, index) => (
            <button
              key={index}
              style={{
                width: "100%",
                padding: "8px",
                marginBottom: "5px",
                cursor: "pointer",
              }}
              onClick={() => addNode(label)}
            >
              {label}
            </button>
          )
        )}
        <button
          style={{
            width: "100%",
            padding: "8px",
            marginTop: "10px",
            background: "green",
            color: "white",
            cursor: "pointer",
          }}
          onClick={saveFlow}
        >
          Save Flow
        </button>
      </div>

      {/* React Flow Canvas */}
      <div style={{ flexGrow: 1 }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </div>
    </div>
  );
}
