import React, { useState, useCallback } from "react";
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  MiniMap,
  Controls,
  Background,
} from "reactflow";
import "reactflow/dist/style.css";

const nodeTypesList = [
  { type: "introduction", label: "ðŸ“– Introduction" },
  { type: "concept", label: "ðŸ’¡ Concept" },
  { type: "quiz", label: "â“ Quiz" },
  { type: "code", label: "ðŸ’» Code Editor" },
  { type: "certificate", label: "ðŸ† Certificate" },
];

const initialNodes = [
  {
    id: "start",
    type: "input",
    position: { x: 250, y: 50 },
    data: { label: "ðŸš€ Start Workshop" },
  },
];

export default function WorkshopFlowDesigner() {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState([]);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  const addNode = (type) => {
    const newNode = {
      id: `${type}-${nodes.length + 1}`,
      position: { x: Math.random() * 600, y: Math.random() * 400 },
      data: { label: nodeTypesList.find((n) => n.type === type).label },
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const saveFlow = () => {
    const flowData = { nodes, edges };
    console.log("âœ… Flow Saved:", flowData);
    alert("Flow Saved! Check console for data.");
    // Here you can POST this data to backend using axios
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Left Sidebar */}
      <div
        style={{
          width: 200,
          padding: 10,
          borderRight: "1px solid #ccc",
          background: "#f8f8f8",
        }}
      >
        <h4>Blocks</h4>
        {nodeTypesList.map((node) => (
          <button
            key={node.type}
            onClick={() => addNode(node.type)}
            style={{
              width: "100%",
              marginBottom: 10,
              padding: "8px 5px",
              cursor: "pointer",
            }}
          >
            {node.label}
          </button>
        ))}

        <button
          onClick={saveFlow}
          style={{
            width: "100%",
            background: "#1976d2",
            color: "#fff",
            padding: "10px",
            border: "none",
            cursor: "pointer",
          }}
        >
          âœ… Save Flow
        </button>
      </div>

      {/* Flow Canvas */}
      <div style={{ flexGrow: 1 }}>
        <ReactFlowProvider>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={setNodes}
            onEdgesChange={setEdges}
            onConnect={onConnect}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background />
          </ReactFlow>
        </ReactFlowProvider>
      </div>
    </div>
  );
}
